// msg.payload is an array of 12 numbers (registers 4101 to 4112)
const staticRegs = msg.payload;

// Helper function for unit type mapping (from table 4101-1)
function getUnitType(lsb) {
  const unitTypes = {
    150: 'Classic 150',
    200: 'Classic 200',
    250: 'Classic 250',
    251: 'Classic 250 KS'
  };
  return unitTypes[lsb] || 'Unknown';
}

const staticInfo = {
  pcb_revision: Number(staticRegs[0]) >> 8,  // 4101 MSB
  unit_type: getUnitType(Number(staticRegs[0]) & 0xFF),  // 4101 LSB
  firmware_date: `${Number(staticRegs[1])}-${String(Number(staticRegs[2]) >> 8).padStart(2, '0')}-${String(Number(staticRegs[2]) & 0xFF).padStart(2, '0')}`,  // 4102-4103
  mac_address: `${Number(staticRegs[7]) >> 8}:${Number(staticRegs[7]) & 0xFF}:${Number(staticRegs[6]) >> 8}:${Number(staticRegs[6]) & 0xFF}:${Number(staticRegs[5]) >> 8}:${Number(staticRegs[5]) & 0xFF}`,  // 4106-4108
  device_id: (Number(staticRegs[11]) << 16) + Number(staticRegs[10]),  // 4111-4112
  whizbang_crc_good: !!(Number(staticRegs[3]) & 0x0001),  // 4104
  wbjr_net_ah: (Number(staticRegs[9]) << 16) + Number(staticRegs[8])  // 4109-4110
};

// Store in global context
global.set('classicStatic', staticInfo);

// Optional: Pass through or debug
msg.payload = staticInfo;
return msg;

// msg.payload is an array of 31 numbers (registers 4114 to 4144)
const dynamicRegs = msg.payload;
const staticInfo = global.get('classicStatic') || {};  // Fallback if not set yet

// Helper functions for mappings (add these)
function getChargeStage(msb) {
    const stages = {
        0: 'Resting',
        3: 'Absorb',
        4: 'BulkMppt',
        5: 'Float',
        6: 'FloatMppt',
        7: 'Equalize',
        10: 'HyperVoc',
        18: 'EqMppt'
    };
    return stages[msb] || 'Unknown';
}

function getChargeState(lsb) {
    const states = {
        0: 'Resting',
        1: 'Waking/Starting',
        2: 'Waking/Starting',
        3: 'MPPT/Regulating Voltage',
        4: 'MPPT/Regulating Voltage',
        6: 'MPPT/Regulating Voltage'
    };
    return states[lsb] || 'Unknown';
}

function getInfoFlags(flags32) {
    flags32 = Number(flags32) || 0;
    const flagMap = {
        0x00000001: 'Classic Over Temperature',
        0x00000002: 'EEPROM error',
        0x00000004: 'SerialWriteLock',
        0x00000008: 'Equalize In Progress',
        0x00000080: 'EQ MPPT',
        0x00000100: 'In V is Lower Than Out',
        0x00000200: 'Current Limit',
        0x00000400: 'HyperVoc',
        0x00002000: 'Battery Temp Sensor Installed',
        0x00004000: 'Aux1 State On',
        0x00008000: 'Aux2 State On',
        0x00010000: 'GroundFaultF',
        0x00020000: 'OCP (Over Current Protect)',
        0x00040000: 'ArcFaultF',
        0x00080000: 'NegBatCurrentF',
        0x00200000: 'XtraInfo2DsplayF',
        0x00400000: 'PvPartialShadeF',
        0x00800000: 'WatchdogResetF',
        0x01000000: 'LowBatteryVF',
        0x02000000: 'StackJumperF',
        0x04000000: 'EqDoneF',
        0x08000000: 'TempCompShortedF',
        0x10000000: 'UN-LockJumperF',
        0x20000000: 'XtraJumperF',
        0x40000000: 'InputShortedF'
    };
    const activeFlags = [];
    for (const [value, desc] of Object.entries(flagMap)) {
        const mask = Number(value) || 0;
        if ((flags32 & mask) !== 0) activeFlags.push(desc);
    }
    return activeFlags;
}

// Parse dynamic (adjusted indices: subtract 13 from original)
const status = {
    restart_timer_ms: Number(dynamicRegs[0]),  // 4114
    battery_voltage: Number(dynamicRegs[1]) / 10,  // 4115
    pv_voltage: Number(dynamicRegs[2]) / 10,  // 4116
    battery_current: Number(dynamicRegs[3]) / 10,  // 4117
    daily_kwh: Number(dynamicRegs[4]) / 10,  // 4118 (for metrics.daily.kwh)
    power_watts: Number(dynamicRegs[5]),  // 4119
    charge_stage: getChargeStage(Number(dynamicRegs[6]) >> 8),  // 4120 MSB
    charge_state: getChargeState(Number(dynamicRegs[6]) & 0xFF),  // 4120 LSB
    pv_current: Number(dynamicRegs[7]) / 10,  // 4121
    last_voc: Number(dynamicRegs[8]) / 10,  // 4122
    highest_input_v: Number(dynamicRegs[9]) / 10,  // 4123
    daily_ah: Number(dynamicRegs[11]),  // 4125 (skip 4124 wind)
    // Lifetime from dynamic (or move to semi-static if desired)
    lifetime_kwh: ((Number(dynamicRegs[13]) << 16) + Number(dynamicRegs[12])),  // 4126-4127
    lifetime_ah: ((Number(dynamicRegs[15]) << 16) + Number(dynamicRegs[14])),  // 4128-4129
    info_flags: getInfoFlags((Number(dynamicRegs[17]) << 16) + Number(dynamicRegs[16])),  // 4130-4131
    battery_temp: Number(dynamicRegs[18]) / 10,  // 4132
    fet_temp: Number(dynamicRegs[19]) / 10,  // 4133
    pcb_temp: Number(dynamicRegs[20]) / 10,  // 4134
    night_minutes_no_pwr: Number(dynamicRegs[21]),  // 4135
    float_time_today_sec: Number(dynamicRegs[24]),  // 4138
    absorb_time_sec: Number(dynamicRegs[25]),  // 4139
    pwm_duty: Number(dynamicRegs[27]),  // 4141
    reset_reason: Number(dynamicRegs[28]),  // 4142
    eq_time_sec: Number(dynamicRegs[29])  // 4143
};

const metrics = {
    daily: {
        kwh: status.daily_kwh,
        ah: status.daily_ah
    },
    lifetime: {
        kwh: status.lifetime_kwh,
        ah: status.lifetime_ah
    }
};

const info = { ...staticInfo };  // Merge static

msg.payload = { status, metrics, info };
return msg;

// msg.payload is an array of 8 uint16 (for str16 Device Name)
const regs = msg.payload;

let deviceName = '';
for (let reg of regs) {
  // LSB first for Conext strings
  deviceName += String.fromCharCode(Number(reg) & 0xFF) + String.fromCharCode((Number(reg) >> 8) & 0xFF);
}

// Trim null chars and extra padding
deviceName = deviceName.replace(/\0/g, '').trim();

// Output as JSON
msg.payload = { info: { device_name: deviceName } };
return msg;

// msg.payload is array of 22 uint16 (regs 141-162 from address 0x008C quantity 22)
const regs = msg.payload;

// Helper for uint32 (little-endian: low word first, so raw = (high << 16) + low)
function uint32(lowIdx) {
  const low = Number(regs[lowIdx]);
  const high = Number(regs[lowIdx + 1]);
  return (high << 16) + low;
}

// Helper for sint32 (signed version)
function sint32(lowIdx) {
  let val = uint32(lowIdx);
  return val > 0x7FFFFFFF ? val - 0x100000000 : val;
}

const ac_load = {
  voltage: uint32(0) / 1000,         // Reg 141-142 (low index 0, high 1)
  current: sint32(10) / 1000,        // Reg 151-152 (low 10, high 11)
  frequency: Number(regs[12]) / 100, // Reg 153 (index 12)
  power_w: sint32(14),               // Reg 155-156 (low 14, high 15)
  power_va: sint32(20)               // Reg 161-162 (low 20, high 21)
};

msg.payload = { ac_load };
return msg;
